/*jslint
    fudge
*/

import pair_type from "./Pair_Type.min.js";


const type_factory = function (type_name) {
    return function (log_type) {
        return function (value_type) {
            const pairT = pair_type (value_type) (log_type);

            // (b->(c,s)) -> (a->(b,s)) -> a -> (c,s)
            const compose = function (f) {
                return function (g) {
                    return function (x) {
                        const gx = g (x);
                        const fx = f (pairT.fst (gx));
/*
                            const embellishment = log_type.concat (
                                log_type.empty ()
                            ) (
                                pairT.snd (gx)
                            );
*/
                        return pairT.create (
                            pairT.fst (fx)
                        ) (
                            log_type.concat (
                                pairT.snd (gx)
                            ) (
                                pairT.snd (fx)
                            )
                        );
                    };
                };
            };

            //  id :: a -> (a, s)
            const id = function (x) {
                return pairT.create (x) (log_type.empty ());
            };

            // Embellish a unary fx with a constant
            const embellish_function = function (f) {
                return function (snd) {
                    return function (x) {
                        return pairT.create (f (x)) (log_type.of (snd));
                    };
                };
            };

            //Needed for adt tests
            const equals = pairT.equals;

            return Object.freeze({
                spec: "StaticLand",
                version: 1,
                typeName: type_name + "< " + value_type.type_name + ">",
                equals,
                compose,
                id,
                embellish_function
            });
        };
    };
};





export default Object.freeze(type_factory);

